import anime from"../anime.min.js";import{pageShown}from"./loadPage.min.js";import{pageElement}from"./main.min.js";function loadImages(){for(const e of pageElement.querySelectorAll("img[data-src]")){e.style.opacity=0;const t=document.createElement("img");t.onload=()=>{e.src=e.getAttribute("data-src"),anime({targets:e,opacity:1,duration:pageShown?250:0,easing:"easeInOutQuad"})},setTimeout(()=>t.src=e.getAttribute("data-src"),0)}}export{loadImages};