import{VectorField}from"/applets/vector-fields/scripts/class.min.js";import{createDesmosGraphs,desmosBlue,desmosPurple}from"/scripts/src/desmos.min.js";import{$,raw}from"/scripts/src/main.min.js";export default function(){createDesmosGraphs({directionField:{bounds:{xmin:-5,xmax:5,ymin:-5,ymax:5},expressions:[{latex:raw`f(t, y) = \frac{\sin(y)}{t^2 + 1}`},{latex:raw`y(t) = 2\arccot(e^{c - \arctan(t)})`,color:desmosBlue},{latex:raw`c = 0`,sliderBounds:{min:-5,max:5}},{latex:raw`n = 10`,sliderBounds:{min:1,max:20,step:1}},{latex:raw`I = [0, 1, ..., (2n + 1)^2 - 1]`,secret:!0},{latex:raw`A = [-n, -n + 1, ..., n]`,secret:!0},{latex:raw`X = A[\mod(I, 2n + 1) + 1]`,secret:!0},{latex:raw`Y = X[\floor(I / (2n + 1)) + 1]`,secret:!0},{latex:raw`f(X, Y)(x - X) + Y \{\left|x - X\right| \leq \frac{.3}{\sqrt{1 + f(X, Y)^2}}\}`,color:desmosPurple,secret:!0}]},directionField2:{bounds:{xmin:-10,xmax:10,ymin:-10,ymax:10},expressions:[{latex:raw`f(t, y) = \frac{1}{50}(y^2 - 4)\left|y - 4\right|(t - 3)`},{latex:raw`n = 10`,sliderBounds:{min:1,max:20,step:1}},{latex:raw`I = [0, 1, ..., (2n + 1)^2 - 1]`,secret:!0},{latex:raw`A = [-n, -n + 1, ..., n]`,secret:!0},{latex:raw`X = A[\mod(I, 2n + 1) + 1]`,secret:!0},{latex:raw`Y = X[\floor(I / (2n + 1)) + 1]`,secret:!0},{latex:raw`f(X, Y)(x - X) + Y \{\left|x - X\right| \leq \frac{.3}{\sqrt{1 + f(X, Y)^2}}\}`,color:desmosPurple,secret:!0}]}});var e=$("#vector-field-canvas");const r=new VectorField({canvas:e});r.loadPromise.then(()=>{r.run({generatingCode:"(1.0, sin(y) / (x*x + 1.0))",worldWidth:8}),r.pauseWhenOffscreen()})}